@push('styles')
    <link href="https://cdn.jsdelivr.net/npm/select2@4.1.0-rc.0/dist/css/select2.min.css" rel="stylesheet" media="print"
        onload="this.media='all'" />
    <script defer src="https://cdn.jsdelivr.net/npm/select2@4.1.0-rc.0/dist/js/select2.min.js"></script>

    <style>
        .select2-container--default .select2-selection--single .select2-selection__rendered {
            color: 1px solid #dce7f1;
            line-height: 38.18px;
        }

        .select2-container .select2-selection--single {
            height: 38.18px;
        }

        .form-control:disabled,
        .form-control[readonly] {
            background-color: transparent !important;
        }

        .btn-reds {
            background-color: #b31217;
            color: #FFFFFF;
            border-color: #b31217;
        }

        .btn-reds:hover {
            background-color: #a10f15;
            border-color: #a10f15;
        }

        .btn-orange {
            background-color: #cc5404;
            color: #FFFFFF;
            border-color: #cc5404;
        }

        .btn-orange:hover {
            background-color: #b54d07;
            border-color: #b54d07;
        }

        .btn-tosca {
            background-color: #046f6f;
            color: #FFFFFF;
            border-color: #046f6f;
        }

        .btn-tosca:hover {
            background-color: #056161;
            border-color: #056161;
        }

        .btn-navy {
            background-color: #005C97;
            color: #FFFFFF;
            border-color: #005C97;
        }

        .btn-navy:hover {
            background-color: #004672;
            border-color: #004672;
        }

        .btn-blues {
            background-color: #2b5876;
            color: #FFFFFF;
            border-color: #2b5876;
        }

        .btn-blues:hover {
            background-color: #284b63;
            border-color: #284b63;
        }

        .btn-light-blue {
            background-color: #4361a8;
            color: #FFFFFF;
            border-color: #4361a8;
        }

        .btn-light-blue:hover {
            background-color: #40539b;
            border-color: #40539b;
        }
    </style>
@endpush
